char_map_t *f6x8_mono_decode_char(Uint8 character)
{
    if(character >= ' ' && character <= '~') //if character between valid table range
        return (char_map_t *)(f6x8f_lut + ((Uint8)(character - ' '))); //calculate correct index and return pointer to map
    else
        return NULL;
}
